import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Auth.css';

const Register = ({ onRegister }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [showValidationModal, setShowValidationModal] = useState(false);
  const [validationMessage, setValidationMessage] = useState('Please fill the empty fields.');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for empty fields
    if (
      !formData.username.trim() ||
      !formData.email.trim() ||
      !formData.password.trim() ||
      !formData.confirmPassword.trim()
    ) {
      setValidationMessage('Please fill the empty fields.');
      setShowValidationModal(true);
      return;
    }

    // Password must be more than 8 characters
    if (formData.password.length <= 8) {
      setValidationMessage('Password must be more than 8 characters.');
      setShowValidationModal(true);
      return;
    }

    // Password must contain at least one capital letter
    if (!/[A-Z]/.test(formData.password)) {
      setValidationMessage('Password must contain at least one capital letter.');
      setShowValidationModal(true);
      return;
    }

    // Password must contain at least one number
    if (!/[0-9]/.test(formData.password)) {
      setValidationMessage('Password must contain at least one number.');
      setShowValidationModal(true);
      return;
    }

    // Check if passwords match
    if (formData.password !== formData.confirmPassword) {
      setValidationMessage('Password did not match.');
      setShowValidationModal(true);
      return;
    }

    // Mock registration - in real app, this would call API
    const mockUser = {
      id: Math.random().toString(36).substr(2, 9),
      username: formData.username,
      email: formData.email,
      token: 'mock-jwt-token-' + Date.now()
    };

    onRegister(mockUser);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>üí¨ Chatroom App</h1>
          <p>Create your account to get started</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">

          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Choose a username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="password-input-wrapper">
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Create a password"
              />
              <button
                type="button"
                className="toggle-password"
                onClick={() => setShowPassword(!showPassword)}
              >
                üëÅÔ∏è
              </button>
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <div className="password-input-wrapper">
              <input
                type={showConfirmPassword ? "text" : "password"}
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                placeholder="Confirm your password"
              />
              <button
                type="button"
                className="toggle-password"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              >
                üëÅÔ∏è
              </button>
            </div>
          </div>

          <button type="submit" className="btn-primary">
            Register
          </button>
        </form>

        <div className="auth-footer">
          <p>Already have an account? <Link to="/login">Login here</Link></p>
        </div>
      </div>

      {showValidationModal && (
        <div className="modal-overlay validation-modal" onClick={() => setShowValidationModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <p>{validationMessage}</p>
            <div className="modal-actions">
              <button
                onClick={() => setShowValidationModal(false)}
                className="btn-submit"
              >
                OK
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Register;
