import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ChatroomList.css';
import user1 from '../img/user1.png';
import user2 from '../img/user2.png';

const ChatroomList = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [chatrooms, setChatrooms] = useState([]);
  const [joinedRooms, setJoinedRooms] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newRoom, setNewRoom] = useState({ name: '', description: '' });
  const [openMenuId, setOpenMenuId] = useState(null);
  const [logoutHover, setLogoutHover] = useState(false);

  // Mock chatrooms data
  useEffect(() => {
    const mockChatrooms = [
      {
        id: '1',
        name: 'General Discussion',
        description: 'General topics and conversations',
        members: 45,
        activeUsers: 12,
        createdBy: 'admin',
        createdAt: new Date('2025-11-01')
      },
      {
        id: '2',
        name: 'Tech Talk',
        description: 'Discuss latest in technology',
        members: 32,
        activeUsers: 8,
        createdBy: 'techguru',
        createdAt: new Date('2025-11-02')
      },
      {
        id: '3',
        name: 'Random Chat',
        description: 'Talk about anything!',
        members: 67,
        activeUsers: 23,
        createdBy: 'user123',
        createdAt: new Date('2025-11-03')
      },
      {
        id: '4',
        name: 'Gaming Zone',
        description: 'For all gaming enthusiasts',
        members: 89,
        activeUsers: 34,
        createdBy: 'gamer',
        createdAt: new Date('2025-11-04')
      }
    ];

    // Load custom rooms from localStorage
    const savedRooms = localStorage.getItem('customChatrooms');
    if (savedRooms) {
      const customRooms = JSON.parse(savedRooms);
      setChatrooms([...mockChatrooms, ...customRooms]);
    } else {
      setChatrooms(mockChatrooms);
    }

    // Load joined rooms for current user
    try {
      const key = `joinedRooms_${user.id}`;
      const joined = JSON.parse(localStorage.getItem(key) || '[]');
      setJoinedRooms(joined);
    } catch (_) { }
  }, []);

  const handleJoinRoom = (roomId, isOpen) => {
    navigate(`/chat/${roomId}`, { state: { fromJoin: !isOpen } });
  };

  const handleDeleteRoom = (roomId) => {
    const updatedRooms = chatrooms.filter(r => r.id !== roomId);
    setChatrooms(updatedRooms);

    // Update custom rooms in localStorage
    const customRooms = updatedRooms.filter(r =>
      !['1', '2', '3', '4'].includes(r.id)
    );
    localStorage.setItem('customChatrooms', JSON.stringify(customRooms));
    setOpenMenuId(null);
  };

  const handleCreateRoom = (e) => {
    e.preventDefault();

    if (!newRoom.name.trim()) {
      alert('Please enter a room name');
      return;
    }

    const room = {
      id: Date.now().toString(),
      name: newRoom.name,
      description: newRoom.description,
      members: 1,
      activeUsers: 1,
      createdBy: user.username,
      createdAt: new Date()
    };

    const updatedRooms = [...chatrooms, room];
    setChatrooms(updatedRooms);

    // Save custom rooms to localStorage
    const customRooms = updatedRooms.filter(r =>
      !['1', '2', '3', '4'].includes(r.id)
    );
    localStorage.setItem('customChatrooms', JSON.stringify(customRooms));

    setNewRoom({ name: '', description: '' });
    setShowCreateModal(false);
  };

  return (
    <div className="chatroom-list-container">
      <div className="chatroom-list-header">
        <div className="header-content">
          <h1>ðŸ’¬ Chatrooms</h1>
          <div className="user-info">
            <span className="welcome-text">Welcome, <strong>{user.username}</strong></span>  {/* THE WELCOME LOGIN PART */}
            <button
              onClick={onLogout}
              className="btn-logout"
              onMouseEnter={() => setLogoutHover(true)}
              onMouseLeave={() => setLogoutHover(false)}
              style={{
                backgroundImage: `url(${logoutHover ? user2 : user1})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
                border: 'none',
                backgroundColor: 'transparent',
                width: '30px',
                height: '30px',
                cursor: 'pointer',
                padding: 0,
                transition: 'transform 0.3s ease'
              }}
            />
          </div>
        </div>
      </div>

      <div className="chatroom-list-content">
        <div className="content-header">
          <h2>Available Chatrooms</h2>
          <button
            onClick={() => setShowCreateModal(true)}
            className="btn-create"
          >
            Create Room
          </button>
        </div>

        <div className="chatrooms-grid">
          {chatrooms.map((room) => (
            <div key={room.id} className="chatroom-card">
              <div className="chatroom-card-header">
                <div className="chatroom-icon">
                  {room.name.charAt(0).toUpperCase()}
                </div>
                {room.createdBy === user.username && (
                  <div className="menu-container">
                    <button
                      className="btn-menu"
                      onClick={() => setOpenMenuId(openMenuId === room.id ? null : room.id)}
                      title="Room options"
                    >
                      â‹®
                    </button>
                    {openMenuId === room.id && (
                      <div className="room-menu">
                        <button
                          className="menu-item delete"
                          onClick={() => handleDeleteRoom(room.id)}
                        >
                          Delete
                        </button>
                      </div>
                    )}
                  </div>
                )}
              </div>
              <div className="chatroom-info">
                <h3>{room.name}</h3>
                <p className="chatroom-description">{room.description}</p>
                <div className="chatroom-stats">
                  <span className="stat">
                    ðŸ‘¥ {room.members} members
                  </span>
                  <span className="stat active">
                    ðŸŸ¢ {room.activeUsers} online
                  </span>
                </div>
              </div>
              {(() => {
                const isJoined = room.createdBy === user.username || joinedRooms.includes(room.id);
                return (
                  <button
                    onClick={() => handleJoinRoom(room.id, isJoined)}
                    className="btn-join"
                  >
                    {isJoined ? 'Open Room' : 'Join Room'}
                  </button>
                );
              })()}
            </div>
          ))}
        </div>
      </div>

      {/* Create Room Modal */}
      {showCreateModal && (
        <div className="modal-overlay" onClick={() => setShowCreateModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Create New Chatroom</h2>
            <form onSubmit={handleCreateRoom}>
              <div className="form-group">
                <label>Room Name *</label>
                <input
                  type="text"
                  value={newRoom.name}
                  onChange={(e) => setNewRoom({ ...newRoom, name: e.target.value })}
                  placeholder="Enter room name"
                  required
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={newRoom.description}
                  onChange={(e) => setNewRoom({ ...newRoom, description: e.target.value })}
                  placeholder="Enter room description (optional)"
                  rows="3"
                />
              </div>
              <div className="modal-actions">
                <button type="button" onClick={() => setShowCreateModal(false)} className="btn-cancel">
                  Cancel
                </button>
                <button type="submit" className="btn-submit">
                  Create Room
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatroomList;
